// Extrair e estruturar dados de alerta do webhook Wazuh (campos body.*)
const items = $input.all();
const processedAlerts = [];

for (const item of items) {
  // Webhook n8n passa payload em item.json.body.{...}
  const root = item.json || {};
  const body = root.body || {};

  // Agente de body.agent.*
  const agentName = body.agent?.name ?? 'agente-desconhecido';
  const agentIp = body.agent?.ip ?? '0.0.0.0';
  const agentId = body.agent?.id ?? 'desconhecido';

  // Regra de body.rule.*
  const ruleIdRaw = body.rule?.id ?? 'desconhecido';
  const ruleLevelRaw = body.rule?.level ?? 0;
  const ruleDescription = body.rule?.description ?? 'Sem descri√ß√£o';
  const ruleGroups = Array.isArray(body.rule?.groups) ? body.rule.groups : [];

  const rule_id = typeof ruleIdRaw === 'string' ? ruleIdRaw : String(ruleIdRaw);
  const rule_level = Number.parseInt(ruleLevelRaw, 10) || 0;

  // Timestamp e localiza√ß√£o de body.*
  const timestamp = body.timestamp ?? new Date().toISOString();
  const location = body.location ?? '';

  // alert_data do campo "message" (ex.: mensagem de log de evento do Windows)
  // No exemplo fornecido: body.data.win.system.message
  const message =
    body.message ??
    body.data?.win?.system?.message ??
    body.full_log ??
    '';

  // Alerta completo tamb√©m mantido (opcionalmente √∫til para depura√ß√£o)
  const alertData = body;

  // ID √∫nico
  const uniqueId = `alert_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;

  const structuredAlert = {
    id: uniqueId,

    // Agente
    agent_name: agentName,
    agent_ip: agentIp,
    agent_id: agentId,

    // alert_data agora vem do campo message
    alert_data: typeof message === 'string' ? message : JSON.stringify(message),

    // Regra
    rule_id,
    rule_level,
    rule_description: ruleDescription,
    rule_groups: ruleGroups.join(','),

    // Campos adicionais
    full_log: '',
    location,

    // Tempo
    timestamp,
    created_at: new Date().toISOString(),

    // Status/origem
    processed: false,
    source: 'wazuh',
    collection_version: 'v1.0',

    // Payload bruto (√∫til para inspe√ß√£o)
    raw_body: JSON.stringify(alertData),
  };

  processedAlerts.push(structuredAlert);
}

return processedAlerts;

// Agrupar alertas por agente E rule_id, filtrar n√£o processados
const alerts = $input.all();

// Filtrar alertas n√£o processados (processado = ‚Äú0‚Äù significa n√£o processado)
const unprocessedAlerts = alerts.filter(item => item.json.processed === "0" || item.json.processed === 0);

// Agrupar por agente + rule_id
const groupedByAgentAndRule = {};

unprocessedAlerts.forEach(item => {
  const agentName = item.json.agent_name;
  const agentIp = item.json.agent_ip;
  const ruleId = item.json.rule_id;
  const key = `${agentName}_${agentIp}_${ruleId}`;

  if (!groupedByAgentAndRule[key]) {
    groupedByAgentAndRule[key] = {
      agent_name: agentName,
      agent_ip: agentIp,
      agent_id: item.json.agent_id,
      rule_id: ruleId,
      rule_description: item.json.rule_description,
      rule_level: item.json.rule_level,
      rule_groups: item.json.rule_groups,
      alerts: [],
      alert_count: 0
    };
  }

  // Criar objeto de alerta estruturado
  groupedByAgentAndRule[key].alerts.push({
    id: item.json.id,
    rule_id: item.json.rule_id,
    rule_level: item.json.rule_level,
    rule_description: item.json.rule_description,
    rule_groups: item.json.rule_groups,
    alert_data: item.json.alert_data,
    timestamp: item.json.timestamp,
    location: item.json.location,
    raw_body: item.json.raw_body
  });
  groupedByAgentAndRule[key].alert_count++;
});

// Converta em matriz e classifique por n√∫mero de alertas (descendente)
const result = Object.values(groupedByAgentAndRule).sort((a, b) => b.alert_count - a.alert_count);

return result.map(item => ({ json: item }));

// üß† Buscar mem√≥ria do agente - √∫ltimos 5 alertas analisados para este agente
const groupedAlerts = $input.all();
const enrichedAlerts = [];

// Obter todos os alertas do node Buscar Alertas N√£o Processados
const allAlertsNode = $('Buscar Alertas N√£o Processados');
const allAlerts = allAlertsNode.all();

// Para cada alerta agrupado, encontrar sua mem√≥ria
for (const groupedItem of groupedAlerts) {
  const agentName = groupedItem.json.agent_name;
  const agentIp = groupedItem.json.agent_ip;

  // Filtrar alertas processados deste agente com vereditos
  const agentMemory = allAlerts
    .filter(item => {
      const alert = item.json;
      return alert.agent_name === agentName &&
             alert.agent_ip === agentIp &&
             (alert.processed === "1" || alert.processed === 1) &&
             alert.verdict != null &&
             alert.verdict !== '';
    })
    .sort((a, b) => {
      const timeA = new Date(a.json.analyzed_at || a.json.timestamp || 0).getTime();
      const timeB = new Date(b.json.analyzed_at || b.json.timestamp || 0).getTime();
      return timeB - timeA;
    })
    .slice(0, 5)
    .map(item => ({
      timestamp: item.json.analyzed_at || item.json.timestamp,
      rule_id: item.json.rule_id,
      rule_description: item.json.rule_description,
      rule_level: item.json.rule_level,
      alert_data: (item.json.alert_data || 'N/A').substring(0, 200),
      verdict: item.json.verdict,
      verdict_confidence: item.json.verdict_confidence,
      ai_summary: item.json.ai_summary
    }));

  enrichedAlerts.push({
    json: {
      ...groupedItem.json,
      agent_memory: agentMemory,
      has_memory: agentMemory.length > 0
    }
  });
}

return enrichedAlerts;

