const alertsByHost = {};
allAlerts.forEach(item => {
  const alert = item.json;
  const hostKey = `${alert.agent_name}_${alert.agent_ip}`;
  if (!alertsByHost[hostKey]) alertsByHost[hostKey] = [];
  alertsByHost[hostKey].push({...alert, alertDate: new Date(alert.analyzed_at || alert.timestamp || alert.created_at)});
});

const mustKeepAlertIds = new Set();
Object.keys(alertsByHost).forEach(hostKey => {
  const processedWithVerdicts = alertsByHost[hostKey]
    .filter(alert => (alert.processed === "1" || alert.processed === 1) && alert.verdict)
    .sort((a, b) => b.alertDate.getTime() - a.alertDate.getTime())
    .slice(0, minAlertsPerHost);
  processedWithVerdicts.forEach(alert => mustKeepAlertIds.add(alert.id));
});

const alertsToDelete = [];
allAlerts.forEach(item => {
  const alert = item.json;
  const alertDate = new Date(alert.analyzed_at || alert.timestamp || alert.created_at);
  const shouldDelete = (alert.processed === "1" || alert.processed === 1) && 
                       alertDate < cutoffDate && 
                       !mustKeepAlertIds.has(alert.id);
  if (shouldDelete) {
    alertsToDelete.push({ json: { id: alert.id } });
  }
});

